jQuery(function($){var button=$('#loadmore a'),paged=button.data('paged'),maxPages=button.data('max_pages');button.click(function(event){event.preventDefault();$.ajax({type:'POST',url:"/wp-admin/admin-ajax.php",data:{paged:paged,catid:button.data('cat'),action:'loadmore'},beforeSend:function(xhr){button.text('Загружаем...')},success:function(data){paged++;$('.errore_table').append(data);button.text('Загрузить ещё');if(paged==maxPages){button.remove()}}})});var button=$('#loadmoretwo a'),paged=button.data('paged'),maxPages=button.data('max_pages');button.click(function(event){event.preventDefault();$.ajax({type:'POST',url:"/wp-admin/admin-ajax.php",data:{paged:paged,catid:button.data('cat_id'),action:'loadmoretwo'},beforeSend:function(xhr){button.text('Загружаем...')},success:function(data){paged++;$('.cat_row').append(data);button.text('Загрузить ещё');if(paged==maxPages){button.remove()}}})})})